const handleGeolocationAutoLocale = () => {
  const explicitLocale = window.sessionStorage.getItem('explicitLocale')
  const autoLocale = window.sessionStorage.getItem('autoLocale')

  if (!explicitLocale) {
    const form = document.querySelector('#localization_form')
    const input = document.querySelector('input[name="language_code"], input[name="locale_code"]')
    const input_country = document.querySelector('input[name="country_code"]')
    
    if (form && input && input_country) {
       fetch('https://apiip.net/api/check?accessKey=b9372a73-cc79-4ac6-b56f-c9dbc660b5ec&output=json&fields=countryCode')
        .then((res) => res.json())
        .then((data) => {
          console.log(data)
          if (data && data.countryCode) {
            if (data.countryCode !== 'DE' && data.countryCode !== 'AT') {
              if (data.countryCode !== input_country.value) {
                // alert("not same")
                input.value = data.countryCode.toString().toLowerCase()
                input_country.value = data.countryCode
                if (!autoLocale || autoLocale !== data.countryCode) {
                  window.sessionStorage.setItem("autoLocale", data.countryCode)
                  // form.submit()
                } else {
                  console.log("AUTO LOCALE", `Using locale from cache - ${autoLocale}`)
                }
              }
            } else {
                console.log("AUTO LOCALE", `Using ${data.countryCode}`)
            }
          }
        }) 
        .catch((err) => {
          console.log("AUTO LOCALE ERROR", err)
        })
    }
  }
}

const handleCountrySearch = (input) => {
  var filter = input.value.toUpperCase();
  var lis = document.querySelectorAll('#CountryList li');
  for (var i = 0; i < lis.length; i++) {
      // var name = lis[i].getElementsByClassName('name')[0].innerHTML;
      var name = lis[i].getAttribute("name")
      if (name.toUpperCase().includes(filter)) 
          lis[i].style.display = 'list-item';
      else
          lis[i].style.display = 'none';
  }
}

